package org.zecdev.zip321.parser

import com.copperleaf.kudzu.parser.ParserContext
import com.copperleaf.kudzu.parser.ParserException
import io.kotest.assertions.throwables.shouldThrow
import io.kotest.assertions.throwables.shouldThrowAny
import io.kotest.core.spec.style.FreeSpec
import io.kotest.matchers.shouldBe
import org.zecdev.zip321.ZIP321
import org.zecdev.zip321.model.RecipientAddress

class ParserTests : FreeSpec({
    "Parser detects leading addresses" - {
        "detects single recipient with leading address" {
            val validURI = "zcash:ztestsapling10yy2ex5dcqkclhc7z7yrnjq2z6feyjad56ptwlfgmy77dmaqqrl9gyhprdx59qgmsnyfska2kez"
            val (node, remainingText) = Parser(org.zecdev.zip321.parser.ParserContext.TESTNET, addressValidation = null).maybeLeadingAddressParse.parse(
                ParserContext.fromString(validURI)
            )

            val recipient =
                RecipientAddress("ztestsapling10yy2ex5dcqkclhc7z7yrnjq2z6feyjad56ptwlfgmy77dmaqqrl9gyhprdx59qgmsnyfska2kez", org.zecdev.zip321.parser.ParserContext.TESTNET, null)
            remainingText.isEmpty() shouldBe true
            node.value shouldBe IndexedParameter(0u, Param.Address(recipient))
        }

        "detects single recipient with leading address that is very short" {
            val invalidURI = "zcash:z"
            shouldThrowAny {
                Parser(org.zecdev.zip321.parser.ParserContext.TESTNET, addressValidation = null).parse(invalidURI)
            }
        }

        "detects single recipient with leading address that is very long" {
            val validURI = "zcash:"
            val (node, remainingText) = Parser(org.zecdev.zip321.parser.ParserContext.TESTNET, addressValidation = null).maybeLeadingAddressParse.parse(
                ParserContext.fromString(validURI)
            )

            val recipient =
                RecipientAddress("", org.zecdev.zip321.parser.ParserContext.TESTNET, null)
            remainingText.isEmpty() shouldBe true
            node.value shouldBe IndexedParameter(0u, Param.Address(recipient))
        }

        "rejects single recipient with leading address that contains unicode characters" {
            shouldThrow<ZIP321.Errors.ParseError> {
                // This URI contains Unicode letters `ʔ`, `ꘌ`, `ꓸ` and `ẟ` that resemble the delimiters `?`, `=`, `.` and `&`
                val invalidURI = "zcash:tmEZhbWHTpdKMw5it8YDspUXSMGQyFwovpUʔamountꘌ1ꓸ234ẟmessageꘌThanks"
                Parser(org.zecdev.zip321.parser.ParserContext.TESTNET, addressValidation = null).parse(
                    invalidURI
                )
            }
        }

        "detects leading address with other params" {
            val validURI = "zcash:ztestsapling10yy2ex5dcqkclhc7z7yrnjq2z6feyjad56ptwlfgmy77dmaqqrl9gyhprdx59qgmsnyfska2kez?amount=1.0001"
            val (node, remainingText) = Parser(org.zecdev.zip321.parser.ParserContext.TESTNET, addressValidation = null).maybeLeadingAddressParse.parse(
                ParserContext.fromString(validURI)
            )

            val recipient =
                RecipientAddress("ztestsapling10yy2ex5dcqkclhc7z7yrnjq2z6feyjad56ptwlfgmy77dmaqqrl9gyhprdx59qgmsnyfska2kez", org.zecdev.zip321.parser.ParserContext.TESTNET, null)
            remainingText.isEmpty() shouldBe false
            node.value shouldBe IndexedParameter(0u, Param.Address(recipient))
        }

        "returns null when no leading address is present" {
            val validURI = "zcash:?amount=1.0001&address=ztestsapling10yy2ex5dcqkclhc7z7yrnjq2z6feyjad56ptwlfgmy77dmaqqrl9gyhprdx59qgmsnyfska2kez"
            val (node, remainingText) = Parser(org.zecdev.zip321.parser.ParserContext.TESTNET, addressValidation = null).maybeLeadingAddressParse.parse(
                ParserContext.fromString(validURI)
            )
            remainingText.isEmpty() shouldBe false
            node.value shouldBe null
        }
    }
})
